import { useState } from 'react';
import rws_app_logo from '../assets/rws_logo.png';

// Funcție pentru a genera inițiala din nume
const getInitial = (name) => {
  if (!name) return 'U';
  return name.charAt(0).toUpperCase();
};

// Funcție pentru a genera o culoare bazată pe inițială
const getColorFromInitial = (initial) => {
  const colors = [
    'bg-red-500', 'bg-blue-500', 'bg-green-500', 'bg-purple-500', 
    'bg-orange-500', 'bg-pink-500', 'bg-teal-500', 'bg-indigo-500',
    'bg-amber-500', 'bg-cyan-500', 'bg-lime-500', 'bg-violet-500'
  ];
  
  const charCode = initial.charCodeAt(0);
  const colorIndex = charCode % colors.length;
  return colors[colorIndex];
};

function Landing({ user, setUser }) {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedNotes, setSelectedNotes] = useState([]);
  const [selectedNote, setSelectedNote] = useState(null);
  const [accountMenuOpen, setAccountMenuOpen] = useState(false);

  const handleNoteClick = (note) => {
    setSelectedNote(note);
  };

  const handleLogout = () => {
    setUser(null);
  };

  // Inițiala și culoarea pentru avatar
  const userInitial = getInitial(user?.name || user?.email);
  const avatarColor = getColorFromInitial(userInitial);

  return (
    <div className="h-screen w-screen flex flex-col bg-[#f6f8fb]">
      {/* Header */}
      <header className="flex items-center justify-between px-8 py-4 bg-white shadow-sm">
        <div className="flex items-center gap-6">
          <img src={rws_app_logo} className="w-24" alt="logo" />
          <input
            type="text"
            placeholder="Search notes..."
            className="px-4 py-2 rounded-lg w-96 border-2 border-[#4E8DC4] shadow-sm focus:outline-none focus:ring-2 focus:ring-[#4E8DC4] focus:border-[#4E8DC4]"
          />
        </div>

        {/* Account dropdown */}
        <div className="relative">
          <button
            onClick={() => setAccountMenuOpen(!accountMenuOpen)}
            className="flex items-center gap-2 px-3 py-2 bg-[#4E8DC4] text-white rounded-lg shadow hover:bg-[#3b78a2] transition-colors"
          >
            {/* Avatar cu imagine sau inițială */}
            {user.pictureUrl ? (
              <img 
                src={user.pictureUrl} 
                alt="Profile" 
                className="w-8 h-8 rounded-full border-2 border-white"
              />
            ) : (
              <div className={`w-8 h-8 rounded-full flex items-center justify-center text-white font-bold ${avatarColor}`}>
                {userInitial}
              </div>
            )}
            <span className="font-medium">
              {user.name?.split(' ')[0] || user.email?.split('@')[0]}
            </span>
            <span className="text-xs">▼</span>
          </button>

          {accountMenuOpen && (
            <div className="absolute right-0 mt-2 w-56 bg-white border rounded-lg shadow-xl z-50">
              {/* Header cu informațiile utilizatorului */}
              <div className="px-4 py-3 border-b bg-gray-50 flex items-center gap-3">
                {user.pictureUrl ? (
                  <img 
                    src={user.pictureUrl} 
                    alt="Profile" 
                    className="w-10 h-10 rounded-full"
                  />
                ) : (
                  <div className={`w-10 h-10 rounded-full flex items-center justify-center text-white font-bold text-lg ${avatarColor}`}>
                    {userInitial}
                  </div>
                )}
                <div className="flex-1 min-w-0">
                  <p className="font-medium text-sm truncate">{user.name}</p>
                </div>
              </div>
              
              {/* Opțiuni */}
              <button className="w-full text-left px-4 py-3 hover:bg-gray-100 transition-colors flex items-center gap-2 border-b">
                <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                </svg>
                Account Details
              </button>
              
              <button
                onClick={handleLogout}
                className="w-full text-left px-4 py-3 hover:bg-gray-100 transition-colors flex items-center gap-2 text-red-600"
              >
                <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1" />
                </svg>
                Logout
              </button>
            </div>
          )}
        </div>
      </header>

      {/* Main content */}
      <div className="flex flex-1 overflow-hidden">
        {/* Left Column */}
        <div className="flex-1 border-r p-4 overflow-auto bg-white">
          <h3 className="font-bold mb-2 text-[#4E8DC4]">Subjects</h3>
          <ul className="mb-4">
            {/* Subjects list will go here */}
          </ul>

          <h3 className="font-bold mb-2 text-[#4E8DC4]">Tags</h3>
          <ul className="mb-4">
            {/* Tags list will go here */}
          </ul>

          <h3 className="font-bold mb-2 text-[#4E8DC4]">Groups</h3>
          <ul>
            {/* Groups list will go here */}
          </ul>
        </div>

        {/* Middle Column */}
        <div className="flex-1 border-r p-4 overflow-auto bg-gray-50">
          <h3 className="font-bold mb-3 text-[#4E8DC4]">Notes</h3>
          {selectedNotes.length === 0 ? (
            <p className="text-gray-500">Select a category to view notes</p>
          ) : (
            <ul>
              {/* Notes list will go here */}
            </ul>
          )}
        </div>

        {/* Right Column */}
        <div className="flex-1 p-4 overflow-auto bg-white">
          <h3 className="font-bold mb-3 text-[#4E8DC4]">Note Details</h3>
          {selectedNote ? (
            <div>
              <h4 className="font-semibold text-lg mb-2">{selectedNote.title}</h4>
              <p>{selectedNote.content}</p>
            </div>
          ) : (
            <p className="text-gray-500">Select a note to view details</p>
          )}
        </div>
      </div>

      {/* Tailwind animation */}
      <style>
        {`
          @keyframes slideDown {
            0% { transform: translateY(-10px); opacity: 0; }
            100% { transform: translateY(0); opacity: 1; }
          }
        `}
      </style>
    </div>
  );
}

export default Landing;
