// components/NoteDetails.jsx
import React from 'react';

export default function NoteDetails({ selectedNote }) {
  if (!selectedNote) {
    return (
      <div className="flex-1 p-4 overflow-auto bg-white">
        <h3 className="font-bold mb-3 text-[#4E8DC4]">Note Details</h3>
        <p className="text-gray-500">Select a note to view details</p>
      </div>
    );
  }

  return (
    <div className="flex-1 p-4 overflow-auto bg-white">
      <h3 className="font-bold mb-3 text-[#4E8DC4]">Note Details</h3>
      <div className="bg-gray-50 p-4 rounded-lg border">
        <h4 className="font-semibold text-lg mb-3 text-[#4E8DC4] border-b pb-2">
          {selectedNote.title}
        </h4>
        <p className="text-gray-700 whitespace-pre-wrap">
          {selectedNote.content}
        </p>
      </div>
    </div>
  );
}
