import React, { useState, useEffect } from 'react';

export default function Subjects({ 
  subjects, 
  setter_subjects, 
  selected_categories, 
  setter_selected_categories, 
  setter_sel, 
  setSelectedNote,
  setter_show_note_user 
}) {
  // State declarations
  const [show_add_subject, settter_show_add_subject] = useState(false);
  const [new_subject_name, setter_new_subject_name] = useState('');
  const [selected_category, setter_selected_category] = useState(null);
  const [md_is_added, setter_md_is_added] = useState(false);

  // Auto-save effect for new subject
  useEffect(() => {
    if (newSubjectName.trim() && isAdding) {
      const timeoutId = setTimeout(() => {
        handleAddSubject();
      }, 1000); // Save after 1 second of inactivity

      return () => clearTimeout(timeoutId);
    }
  }, [new_subject_name, md_is_added]);

  const handleAddSubject = () => {
    if (newSubjectName.trim()) {
      const newSubject = {
        id: Date.now().toString(),
        name: newSubjectName.trim(),
        notes: []
      };
      setter_subjects([...subjects, newSubject]);
      setNewSubjectName('');
      setShowAddSubject(false);
      setIsAdding(false);
    }
  };

  const handleSubjectClick = (subject) => {
    setSelectedCategory(subject);
    // Add any additional logic you need when a subject is clicked
  };

  const handleDeleteSubject = (subjectId, e) => {
    e.stopPropagation();
    const updatedSubjects = subjects.filter(subject => subject.id !== subjectId);
    setter_subjects(updatedSubjects);
    
    if (selectedCategory?.id === subjectId) {
      setSelectedCategory(null);
    }
  };

  const startAddingSubject = () => {
    setShowAddSubject(true);
    setIsAdding(true);
    setNewSubjectName('');
  };

  const cancelAddSubject = () => {
    setShowAddSubject(false);
    setIsAdding(false);
    setNewSubjectName('');
  };

  return (
    <div className="mb-6">
      <div className="flex justify-between items-center mb-2">
        <h3 className="font-bold text-[#4E8DC4]">Subjects</h3>
        <button
          onClick={startAddingSubject}
          className="text-[#4E8DC4] hover:text-[#3b78a2] transition-colors"
          title="Add new subject"
        >
          <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
          </svg>
        </button>
      </div>

      {showAddSubject && (
        <div className="mb-4 p-3 bg-gray-50 rounded-lg border">
          <input
            type="text"
            value={newSubjectName}
            onChange={(e) => setNewSubjectName(e.target.value)}
            placeholder="Enter subject name... (auto-saves)"
            className="w-full px-3 py-2 border rounded mb-2 focus:outline-none focus:ring-2 focus:ring-[#4E8DC4]"
            autoFocus
          />
          <div className="flex gap-2">
            <button
              onClick={handleAddSubject}
              className="flex-1 bg-[#4E8DC4] text-white py-1 px-3 rounded hover:bg-[#3b78a2] transition-colors text-sm"
            >
              Save Now
            </button>
            <button
              onClick={cancelAddSubject}
              className="flex-1 bg-gray-300 text-gray-700 py-1 px-3 rounded hover:bg-gray-400 transition-colors text-sm"
            >
              Cancel
            </button>
          </div>
          {newSubjectName.trim() && (
            <p className="text-xs text-gray-500 mt-2">Subject will be saved automatically...</p>
          )}
        </div>
      )}

      <ul className="space-y-1">
        {subjects.length === 0 ? (
          <li className="text-gray-500 text-sm py-2 text-center">No subjects yet</li>
        ) : (
          subjects.map((subject) => (
            <li
              key={subject.id}
              className={`cursor-pointer px-3 py-2 rounded hover:bg-[#E3F0FF] transition-colors group flex justify-between items-center ${
                selectedCategory?.id === subject.id ? 'bg-[#4E8DC4] text-white' : ''
              }`}
              onClick={() => handleSubjectClick(subject)}
            >
              <span className="flex-1 truncate">{subject.name}</span>
              <span className="text-xs bg-gray-200 text-gray-600 px-2 py-1 rounded-full">
                {subject.notes?.length || 0}
              </span>
              <button
                onClick={(e) => handleDeleteSubject(subject.id, e)}
                className={`ml-2 opacity-0 group-hover:opacity-100 transition-opacity ${
                  selectedCategory?.id === subject.id ? 'text-white hover:text-red-200' : 'text-gray-400 hover:text-red-500'
                }`}
                title="Delete subject"
              >
                <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </li>
          ))
        )}
      </ul>
    </div>
  );
}
