import React, { useState, useEffect } from 'react';
import Subjects from './subjects_tabs_groups_/Subjects';
import Tags from './subjects_tabs_groups_/Tags';
import Groups from './subjects_tabs_groups_/Groups';

export default function SubjectsTagsGroupsBar({
  subjects,
  setter_subjects,
  selected_categories,
  setter_selected_categories,
  setter_selected_note,
  setter_show_note_user
}) {

  // --- STATE PENTRU GRUPURI ---
  const [groups, setGroups] = useState([]);

  // --- FETCH GRUPURI (La incarcare) ---
  useEffect(() => {
    const fetchGroups = async () => {
      try {
        const resp = await fetch("http://localhost:5019/groups", { credentials: "include" });
        if (resp.ok) {
          const data = await resp.json();
          setGroups(data);
        }
      } catch (err) {
        console.error("Failed to load groups", err);
      }
    };
    fetchGroups();
  }, []); // Ruleaza o singura data la montare

  return (
    <div className="h-full flex flex-col space-y-6">

      {/* Subjects Section */}
      <div className="bg-white rounded-lg shadow-sm border p-4">
        <Subjects
          subjects={subjects || []}
          setter_subjects={setter_subjects}
          selected_categories={selected_categories}
          setter_selected_categories={setter_selected_categories}
          setter_sel={setter_selected_categories}
          setSelectedNote={setter_selected_note}
          setter_show_note_user={setter_show_note_user}
        />
      </div>

      {/* Tags Section */}
      <div className="bg-white rounded-lg shadow-sm border p-4">
        <Tags 
           // Probabil vei avea nevoie de props si aici pe viitor
        />
      </div>

      {/* Groups Section */}
      <div className="bg-white rounded-lg shadow-sm border p-4">
        {/* AICI ESTE MODIFICAREA IMPORTANTA: Trimitem props! */}
        <Groups 
          groups={groups}
          setter_groups={setGroups}
          setter_selected_categories={setter_selected_categories}
        />
      </div>
    </div>
  );
}
