import React from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import App from './App';
import Landing from './components/Landing';

// Componentă simplă pentru Landing care verifică user-ul
const ProtectedLanding = () => {
  const [user, setUser] = React.useState(null);
  const [loading, setLoading] = React.useState(true);

  React.useEffect(() => {
    const checkAuth = async () => {
      try {
        const res = await fetch("http://localhost:5000/auth/status", {
          credentials: "include",
        });

        if (res.ok) {
          const data = await res.json();
          setUser(data?.user || data);
        } else {
          // Dacă nu este autentificat, redirect către login
          window.location.href = '/';
        }
      } catch (err) {
        console.error("Auth error:", err);
        window.location.href = '/';
      } finally {
        setLoading(false);
      }
    };

    checkAuth();
  }, []);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="w-12 h-12 border-4 border-[#4E8DC4] border-t-transparent rounded-full animate-spin"></div>
      </div>
    );
  }

  if (!user) return null; // Se va redirecta în useEffect

  return <Landing user={user} setUser={setUser} />;
};

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <Router>
    <Routes>
      <Route path="/" element={<App />} />
      <Route path="/landing" element={<ProtectedLanding />} />
    </Routes>
  </Router>
);
