import rws_app_logo from './assets/rws_logo.png';
import { useState, useEffect } from 'react';
import { motion } from 'framer-motion';

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // check user status
  useEffect(() => {
    const checkAuthStatus = async () => {
      try {
        const res = await fetch("http://localhost:5000/auth/status", {
          credentials: "include",
        });
        const data = await res.json();
        if (data.user) setUser(data.user);
      } catch (err) {
        console.error("Error checking auth status:", err);
      }
      setLoading(false);
    };
    checkAuthStatus();
  }, []);

  const handleGoogleLogin = () => {
    window.location.href = "http://localhost:5000/auth/google";
  };

  const handleLogout = async () => {
    await fetch("http://localhost:5000/logout", { credentials: "include" });
    setUser(null);
  };

  // loading spinner
  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="w-12 h-12 border-4 border-[#4E8DC4] border-t-transparent rounded-full animate-spin"></div>
      </div>
    );
  }

  // ✅ IF USER IS LOGGED IN → SHOW LANDING PAGE
  if (user) {
    return (
      <div className="h-screen w-screen flex flex-col bg-[#f6f8fb]">

        {/* HEADER */}
        <header className="flex items-center justify-between px-6 py-4 border-b bg-white shadow-sm">
          <div className="flex items-center gap-4">
            <img src={rws_app_logo} className="w-12" />
            <input
              type="text"
              placeholder="Search notes..."
              className="px-4 py-2 border rounded-lg w-72"
            />
          </div>

          <div className="flex items-center gap-3">
            <span className="font-semibold text-[#4E8DC4]">
              {user.email}
            </span>
            <button
              onClick={handleLogout}
              className="text-sm underline text-[#1f2937]"
            >
              Logout
            </button>
          </div>
        </header>

        {/* 3 COLUMN LAYOUT */}
        <div className="flex flex-1 overflow-hidden">

          {/* LEFT COLUMN */}
          <div className="w-1/4 border-r p-4 overflow-auto">
            <h3 className="font-bold mb-3 text-[#4E8DC4]">Subjects</h3>
            <h3 className="font-bold mt-6 mb-3 text-[#4E8DC4]">Tags</h3>
            <h3 className="font-bold mt-6 mb-3 text-[#4E8DC4]">Groups</h3>
          </div>

          {/* MIDDLE COLUMN */}
          <div className="w-1/3 border-r p-4 overflow-auto">
            <h3 className="font-bold mb-3 text-[#4E8DC4]">Notes</h3>
          </div>

          {/* RIGHT COLUMN */}
          <div className="flex-1 p-4 overflow-auto">
            <h3 className="font-bold mb-3 text-[#4E8DC4]">Note Details</h3>
          </div>

        </div>
      </div>
    );
  }

  // ✅ IF NOT LOGGED IN → SHOW YOUR LOGIN PAGE
  return (
    <div className="relative min-h-screen flex items-center justify-center overflow-hidden bg-gradient-to-br from-[#f9fafb] via-[#eef3f8] to-[#e6edf5] text-[#1f2937]">

      {/* background lights */}
      <div className="absolute inset-0 overflow-hidden">
        <motion.div
          className="absolute w-[20rem] h-[20rem] bg-[#FEDB59]/70 rounded-full blur-[100px]"
          animate={{ x: ['0%', '60%', '-40%', '0%'], y: ['0%', '-30%', '40%', '0%'] }}
          transition={{ duration: 20, repeat: Infinity, ease: 'easeInOut' }}
          style={{ top: '10%', left: '5%' }}
        />
        <motion.div
          className="absolute w-[22rem] h-[22rem] bg-[#4E8DC4]/60 rounded-full blur-[110px]"
          animate={{ x: ['0%', '-50%', '60%', '0%'], y: ['0%', '40%', '-30%', '0%'] }}
          transition={{ duration: 25, repeat: Infinity, ease: 'easeInOut' }}
          style={{ bottom: '10%', right: '8%' }}
        />
      </div>

      {/* main */}
      <motion.div
        initial={{ opacity: 0, scale: 0.9, y: 40 }}
        animate={{ opacity: 1, scale: 1, y: 0 }}
        transition={{ duration: 0.8, ease: 'easeOut' }}
        className="relative z-10 bg-white/90 backdrop-blur-xl border border-[#4E8DC4]/40 rounded-3xl shadow-2xl flex overflow-hidden w-11/12 max-w-5xl"
        style={{
          boxShadow: `
            -35px 0 70px -10px rgba(78,141,196,0.55),
            35px 0 70px -10px rgba(254,219,89,0.55)
          `,
        }}
      >

        {/* left panel */}
        <div
          className="w-1/2 hidden md:flex flex-col items-center justify-center p-10 relative"
          style={{ backgroundColor: "#76AAE0", color: "#ffffff" }}
        >
          <motion.img
            src={rws_app_logo}
            alt="logo"
            className="w-36 h-36 object-contain relative z-10 mb-6"
            style={{
              filter: `
                drop-shadow(10px 0 25px rgba(255,255,255,0.95))
                drop-shadow(0 0 35px rgba(255,255,255,0.9))
                drop-shadow(-5px 0 15px rgba(255,255,255,0.7))
              `,
            }}
            initial={{ rotate: -15, opacity: 0, scale: 0.5 }}
            animate={{ rotate: 0, opacity: 1, scale: 1 }}
            transition={{
              duration: 1.8,
              ease: "easeOut",
              type: "spring",
              stiffness: 70,
              damping: 10,
            }}
          />
          <h1 className="text-2xl font-extrabold text-center mb-2 drop-shadow-sm">
            RWS Productivity
          </h1>
          <p className="text-sm font-medium text-center opacity-90 max-w-xs">
            Stay organized. Stay focused. Achieve more with ease.
          </p>
        </div>

        {/* right panel */}
        <div className="w-full md:w-1/2 p-10 flex flex-col justify-center">
          <h2
            className="text-3xl font-extrabold mb-6 text-center bg-gradient-to-r from-[#4E8DC4] via-[#76AAE0] to-[#FEDB59] bg-clip-text text-transparent"
          >
            Welcome Back!
          </h2>

          <p className="text-center text-[#4E8DC4]/80 mb-6">
            Sign in using your institutional email address
          </p>

          <button
            onClick={handleGoogleLogin}
            className="mx-auto flex items-center justify-center gap-3 bg-white border border-[#ccc] rounded-full px-6 py-3 shadow-md hover:shadow-lg transition"
          >
            <img
              src="https://developers.google.com/identity/images/g-logo.png"
              alt="Google"
              className="w-5 h-5"
            />
            <span className="text-[#4e8dc4] font-medium">
              Sign in with Google
            </span>
          </button>

        </div>
      </motion.div>
    </div>
  );
}

export default App;
